String styleSheet = "body{max-width:1000px;margin:auto;background-color:#fff}*{font-family:SFMono-Regular,Menlo,Monaco,Consolas,'Liberation Mono','Courier New',monospace}h1{font-size:4rem}h3{margin:20px 0 5px 0;font-size:3rem}form{font-size:0;padding:0 20px}input{width:30%;padding:1.5rem 1rem;margin:4px 0;border:1px solid #ccc;box-sizing:border-box;font-size:2rem}input[type=text]{display:none}input[type=date]{border-radius:.75rem 0 0 .75rem}input[type=date]:read-only{background-color:#d2d2d2}input[type=submit]{border-radius:0 .75rem .75rem 0;background-color:#007bff;color:#fff;padding:1.75rem 1rem}.birthday>input[type=date]{width:40%}.setTime>input[type=submit]{width:20%}.findTime>input[type=date],.findTime>input[type=time]{display:none}.findTime>input[type=submit]{border-radius:.75rem;width:auto}.exit>input[type=submit]{border-radius:.75rem;width:30%}.remove>input[type=submit]{width:20%;background-color:#dc3545}.add>input[type=submit]{width:20%;background-color:#28a745}.capture>input[type=submit]{width:50%}.dim>input[type=submit]{border-radius:.75rem 0 0 .75rem;float:left;background-color:#766c54;color:#ecdaac}.bright>input[type=submit]{float:right;background-color:#ecdaac;color:#766c54}.brightness{margin-top:8rem}.brightness>input[type=submit]{width:100%;margin:0;border:0;border-radius:0 0 .75rem .75rem}.log{font-size:2rem;border:1px solid #6c757d;border-radius:.75rem;display:inline-block;padding:.2rem 1rem}.multi-range,.multi-range *{box-sizing:border-box;padding:0;margin:0}.multi-range{position:relative;width:100%;height:8rem;border:0 solid #000;font-family:monospace;background:#473f2d;background:linear-gradient(90deg,rgba(71,63,45,1) 0,rgba(255,226,152,1) 100%);border-radius:.75rem .75rem 0 0;-webkit-appearance:none}.multi-range>hr{position:absolute;width:100%;top:50%}.multi-range>input[type=range]{position:absolute;bottom:4rem;left:.5%;-webkit-appearance:none;border:0 solid #ccc;width:99%;height:0;border-radius:0;outline:0;opacity:1}.multi-range>input[type=range]::-webkit-slider-thumb{transform:translateY(-3.5rem);-webkit-appearance:none;appearance:none;width:3rem;height:7rem;border-radius:.75rem;background:#000;opacity:.5;cursor:pointer}.multi-range>input[type=range]::-webkit-slider-runnable-track{-webkit-appearance:none;height:0}.multi-range>input[type=range]::-webkit-slider-runnable-track{-webkit-appearance:none;height:0}.multi-range>input[type=range]::-moz-range-thumb{transform:translateY(-2.7rem)}.multi-range>input[type=range]::-moz-range-track{-webkit-appearance:none;height:0}.multi-range>input[type=range]::-ms-thumb{transform:translateY(-2.7rem)}.multi-range>input[type=range]::-ms-track{-webkit-appearance:none;height:0}.multi-range::before{content:'Min ' attr(data-lbound);position:absolute;top:10rem;left:15%;white-space:nowrap;display:block;padding:0 4px;margin:-1px -10%;height:auto;width:auto;border:0 solid #ddd;line-height:26px;font-size:2rem;color:#fff}.multi-range::after{content:'Max ' attr(data-ubound);position:absolute;top:10rem;left:82%;white-space:nowrap;display:block;padding:0 4px;margin:-1px 0;height:26px;width:7%;border:0 solid #ddd;line-height:26px;font-size:2rem;color:#fff;text-align:right}";
